 // DEPENDENCIES
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { Courses } from '../../domain/jmyx_prototype_db/courses';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../Courses.service.ts
 */

/*
 * SCHEMA DB Courses
 *
	{
		class_time: {
			type: 'Date'
		},
		create_time: {
			type: 'Date',
			required : true
		},
		level: {
			type: 'String',
			required : true
		},
		name: {
			type: 'String',
			required : true
		},
		update_time: {
			type: 'Date',
			required : true
		},
		//RELATIONS
		//EXTERNAL RELATIONS
		student_course: [{
			type: Schema.ObjectId,
			ref : "Students"
		}],
		teacher_class: {
			type: Schema.ObjectId,
			ref : "Teachers"
		},
	}
 *
 */
@Injectable()
export class CoursesBaseService {

    contextUrl: string = environment.endpoint + '/courses';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * CoursesService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: Courses): Observable<Courses> {
        return this.http
            .post<Courses>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * CoursesService.delete
    *   @description CRUD ACTION delete
    *   @param ObjectId id Id
    *
    */
    remove(id: string): Observable<void> {
        return this.http
            .delete<void>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * CoursesService.get
    *   @description CRUD ACTION get
    *   @param ObjectId id Id resource
    *
    */
    get(id: string): Observable<Courses> {
        return this.http
            .get<Courses>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * CoursesService.list
    *   @description CRUD ACTION list
    *
    */
    list(): Observable<Courses[]> {
        return this.http
            .get<Courses[]>(this.contextUrl)
            .pipe(map(data => data));
    }

    /**
    * CoursesService.update
    *   @description CRUD ACTION update
    *   @param ObjectId id Id
    *
    */
    update(item: Courses): Observable<Courses> {
        return this.http
            .post<Courses>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs

}
